function Paginator (req) {
  this.page = parseInt(req.query.p || 1, 10);
  this.countPerPage = parseInt(req.query.cpp || 10, 10);
};

Paginator.prototype.paginateQuery = function (query) {
  return query
  .skip((this.page - 1) * this.countPerPage)
  .limit(this.countPerPage);
};


module.exports = exports = Paginator;
