module.exports = function (app) {
  this.app = app;
};

module.exports.prototype.add = function (route) {
  console.log('adding route:', route.method, route.uri);
  if (route.controllerMethod == undefined) {
    console.log('ERROR: invalid/undefined controller method', route);
    return;
  }
  switch (route.method) {
    case 'POST':
      this.app.post(route.uri, route.controllerMethod);
      break;
    case 'GET':
      this.app.get(route.uri, route.controllerMethod);
      break;
    case 'PUT':
      this.app.put(route.uri, route.controllerMethod);
      break;
    case 'DELETE':
      this.app.delete(route.uri, route.controllerMethod);
      break;
      
    default:
      console.log('unsupported HTTP method for route: ', route);
      break;
  }
};
